// Text là widget để hiển thị chuỗi văn bản
Text(
  'Xin chào Flutter!',
  style: TextStyle(
    fontSize: 20, // Cỡ chữ
    fontWeight: FontWeight.bold, // Đậm chữ
    color: Colors.deepPurple, // Màu chữ
    letterSpacing: 1.5, // Khoảng cách giữa các chữ cái
  ),
  textAlign: TextAlign.center, // Căn giữa văn bản
);

// Image widget để hiển thị hình ảnh từ internet hoặc assets
Image.network(
  'https://flutter.dev/assets/images/shared/brand/flutter/logo/flutter-lockup.png',
  width: 120, // Chiều rộng ảnh
  height: 120, // Chiều cao ảnh
  fit: BoxFit.cover, // Cách ảnh lấp đầy vùng chứa
);

// Icon widget dùng để hiển thị biểu tượng
Icon(
  Icons.favorite,
  color: Colors.redAccent, // Màu của icon
  size: 36, // Kích thước icon
);

// Card là widget để hiển thị hộp có bóng đổ
Card(
  elevation: 6, // Độ nổi (bóng đổ)
  margin: EdgeInsets.all(12), // Khoảng cách xung quanh
  shape: RoundedRectangleBorder(
    borderRadius: BorderRadius.circular(10), // Bo góc
  ),
  child: Padding(
    padding: EdgeInsets.all(16), // Khoảng cách bên trong
    child: Text('Đây là nội dung trong Card'),
  ),
);

// Divider là đường kẻ ngang chia cách các phần
Divider(
  color: Colors.grey, // Màu của đường kẻ
  thickness: 1.5, // Độ dày đường kẻ
  indent: 20, // Lề trái
  endIndent: 20, // Lề phải
);

// Opacity giúp điều chỉnh độ trong suốt của widget
Opacity(
  opacity: 0.5, // Mức độ hiển thị (0 là trong suốt, 1 là rõ)
  child: Icon(
    Icons.visibility,
    size: 48,
    color: Colors.blue,
  ),
);
