// MaterialApp là widget gốc tạo nên cấu trúc cơ bản của app
MaterialApp(
  title: 'Ứng dụng Flutter Demo',
  theme: ThemeData(
    primarySwatch: Colors.teal, // Màu chủ đạo
  ),
  home: MyHomePage(), // Màn hình đầu tiên khi app chạy
);

// Scaffold tạo bố cục cơ bản: AppBar, body, FAB, Drawer...
Scaffold(
  appBar: AppBar(
    title: Text('Trang chủ'),
  ),
  body: Center(
    child: Text('Nội dung chính'),
  ),
  floatingActionButton: FloatingActionButton(
    onPressed: () {},
    child: Icon(Icons.add),
  ),
);

// AppBar là thanh tiêu đề ở đầu màn hình
AppBar(
  title: Text('Tiêu đề trang'),
  actions: [
    IconButton(
      icon: Icon(Icons.settings),
      onPressed: () {},
    ),
  ],
);

// Thanh điều hướng dưới đáy màn hình
BottomNavigationBar(
  currentIndex: 0,
  onTap: (index) {
    // xử lý khi bấm vào tab
  },
  items: [
    BottomNavigationBarItem(
      icon: Icon(Icons.home),
      label: 'Trang chủ',
    ),
    BottomNavigationBarItem(
      icon: Icon(Icons.person),
      label: 'Tài khoản',
    ),
  ],
);

// Drawer là menu bên trái trượt vào
Drawer(
  child: ListView(
    children: [
      DrawerHeader(
        decoration: BoxDecoration(color: Colors.blue),
        child: Text('Xin chào bạn!'),
      ),
      ListTile(
        title: Text('Trang chủ'),
        leading: Icon(Icons.home),
        onTap: () {},
      ),
    ],
  ),
);

// SafeArea tránh vùng bị che bởi tai thỏ, status bar...
SafeArea(
  child: Text('Nội dung an toàn!'),
);
