// Chuyển sang màn hình mới (đẩy vào stack)
Navigator.push(
  context,
  MaterialPageRoute(builder: (context) => SecondPage()),
);

// Điều hướng bằng thanh tab dưới cùng
BottomNavigationBar(
  currentIndex: selectedIndex,
  onTap: (index) {
    setState(() => selectedIndex = index);
  },
  items: const [
    BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Trang chủ'),
    BottomNavigationBarItem(icon: Icon(Icons.settings), label: 'Cài đặt'),
  ],
);

// TabBar cho tiêu đề tab, TabBarView cho nội dung từng tab
DefaultTabController(
  length: 3,
  child: Scaffold(
    appBar: AppBar(
      bottom: TabBar(
        tabs: [Tab(text: 'A'), Tab(text: 'B'), Tab(text: 'C')],
      ),
    ),
    body: TabBarView(
      children: [Text('Tab A'), Text('Tab B'), Text('Tab C')],
    ),
  ),
);

// Chuyển trang theo dạng cuộn ngang
PageView(
  children: [
    Container(color: Colors.red),
    Container(color: Colors.green),
    Container(color: Colors.blue),
  ],
);
